import React, { useState, useEffect } from "react";
import "../CSS/Upload.css";

function Upload() {
  const [files, setFiles] = useState([]);
  const [dragActive, setDragActive] = useState(false);

  useEffect(() => {
    const preventDefault = (e) => {
      e.preventDefault();
    };

    window.addEventListener("dragover", preventDefault);
    window.addEventListener("drop", preventDefault);

    return () => {
      window.removeEventListener("dragover", preventDefault);
      window.removeEventListener("drop", preventDefault);
    };
  }, []);

  const formatBytes = (bytes, decimals = 2) => {
    if (bytes === 0) return "0 Bytes";
    const k = 1024;
    const dm = decimals < 0 ? 0 : decimals;
    const sizes = ["Bytes", "KB", "MB", "GB", "TB", "PB", "EB", "ZB", "YB"];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(dm)) + " " + sizes[i];
  };

  const calculateTotalSize = () => {
    const totalBytes = files.reduce((total, file) => {
      if (file.selected) {
        console.log("Adding size:", file.sizeInBytes); // Debugging log
        return total + file.sizeInBytes;
      } else {
        return total;
      }
    }, 0);

    console.log("Total size in bytes:", totalBytes); // Debugging log
    return formatBytes(totalBytes);
  };

  const isAnyFileSelected = () => {
    return files.some((file) => file.selected);
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    const newFiles = e.dataTransfer.files;
    if (newFiles) {
      handleFiles(newFiles);
    }
  };

  const handleChange = (e) => {
    const newFiles = e.target.files;
    if (newFiles) {
      handleFiles(newFiles);
    }
  };

  const handleFiles = (newFiles) => {
    const mappedFiles = Array.from(newFiles).map((file) => {
      const previewURL = file.type.startsWith("image/") & file.type.startsWith("mp4/");
      return {
        name: file.name,
        size: formatBytes(file.size),
        sizeInBytes: file.size,
        selected: false,
        previewURL, // Add this line
      };
    });
    setFiles((prevFiles) => [...prevFiles, ...mappedFiles]);
  };


  const toggleFileSelection = (index) => {
    setFiles((prevFiles) =>
      prevFiles.map((file, i) =>
        i === index ? { ...file, selected: !file.selected } : file
      )
    );
  };

  const toggleSelectAll = () => {
    const allSelected = files.every((file) => file.selected);
    setFiles(files.map((file) => ({ ...file, selected: !allSelected })));
  };

  const formatFileName = (name) => {
    const extension = name.split(".").pop();
    const baseName = name.substring(0, name.lastIndexOf("."));
    const truncatedBaseName =
      baseName.length > 8 ? baseName.substring(0, 8) + "..." : baseName;
    return `${truncatedBaseName}.${extension}`;
  };

  const dummyUploadFiles = () => {
    // Check if any file is selected
    if (isAnyFileSelected()) {
      // Displaying a message to the user
      alert("Sending selected file(s) to the server...");

      // For a more realistic simulation, you can use setTimeout
      setTimeout(() => {
        alert("File(s) have been successfully sent to the server!");
      }, 1000); // 1 second delay

      // Here, you would typically make an API call to your server
      console.log(
        "Files to upload:",
        files.filter((file) => file.selected)
      );
    } else {
      alert("Please select at least one file to upload.");
    }
  };

  return (
    <div className="upload-container">
      <form
        className={`drop-zone ${dragActive ? "active" : ""}`}
        onDragEnter={handleDrag}
        onDragOver={handleDrag}
        onDragLeave={handleDrag}
        onDrop={handleDrop}
      >
        <input
          id="file-upload"
          type="file"
          className="file-input"
          onChange={handleChange}
          multiple
        />
        <div className="drop-message">
          Drag and drop files here or{" "}
          <label htmlFor="file-upload" className="upload-link">
            browse
          </label>
        </div>
        {files.length > 0 && (
          <div>
            <div className="select-all">
              <input
                type="checkbox"
                onChange={toggleSelectAll}
                checked={files.every((file) => file.selected)}
              />
              Select All
            </div>
            <div className="file-display-container">
              {files.map((file, index) => (
                <div className="file-status-bar" key={index}>
                  <input
                    type="checkbox"
                    checked={file.selected}
                    onChange={() => toggleFileSelection(index)}
                  />
                  {file.previewURL && (
                    <img
                      src={file.previewURL}
                      alt="Preview"
                      style={{ width: "50px", height: "50px" }}
                    />
                  )}
                  <div className="file-info">
                    <span className="file-name" title={file.name}>
                      {formatFileName(file.name)}
                    </span>
                    <span className="file-size">({file.size})</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </form>
      {isAnyFileSelected() && (
        <div className="totalsize">Total Size: {calculateTotalSize()}</div>
      )}
      <button onClick={dummyUploadFiles}>Upload</button>
    </div>
  );
}

export default Upload;
