import React, { useState } from "react";
import "../CSS/Upload.css";

function Upload() {
  const [dragActive, setDragActive] = useState(false);

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === 'dragenter' || e.type === 'dragover') {
      setDragActive(true);
    } else if (e.type === 'dragleave') {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    const files = e.dataTransfer.files;
    console.log(files);
  };

  const handleChange = (e) => {
    const files = e.target.files;
    console.log(files);
  };

  return (
    <div className="upload-container">
      <div className="logo">KITE</div>
      <form className={`drop-zone ${dragActive ? 'active' : ''}`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}>
        <p className="drop-message">Drag and drop or <span className="file-input-label">Choose file</span> from computer.</p>
        <input type="file" id="file-input" className="file-input" onChange={handleChange} multiple />
        <label htmlFor="file-input" className="file-input-button">Choose file</label>
      </form>
    </div>
  );
}

export default Upload;
