import React, { useState } from "react";
import "../CSS/Access.css";

function Access() {
  const [files, setFiles] = useState([
    {
      name: "Karina.png",
      type: "PNG",
      date: "12/12/2023",
      size: "3.4 MB",
      checked: false,
    },
    {
      name: "Iftesam.jpeg",
      type: "JPEG",
      date: "12/12/2023",
      size: "2.3 MB",
      checked: false,
    },
    {
      name: "Taylar.mp4",
      type: "MP4",
      date: "12/12/2023",
      size: "88 MB",
      checked: false,
    },
    {
      name: "Ethan.wav",
      type: "WAV",
      date: "12/12/2023",
      size: "9.8 MB",
      checked: false,
    },
    {
      name: "Capstone.pdf",
      type: "PDF",
      date: "12/12/2023",
      size: "2.3 MB",
      checked: false,
    },
  ]);
  const [selectAll, setSelectAll] = useState(false);

  const handleSelectAll = (event) => {
    const checked = event.target.checked;
    setSelectAll(checked);
    setFiles(files.map((file) => ({ ...file, checked })));
  };

  const handleCheck = (index) => {
    const newFiles = [...files];
    newFiles[index].checked = !newFiles[index].checked;
    setFiles(newFiles);
    setSelectAll(newFiles.every((file) => file.checked));
  };

  return (
    <div className="access-container">
      <div className="access-number">318278</div>
      <button className="access-button">ACCESS FILE</button>
      <div className="file-list">
        <div className="file-item select-all">
          <input
            type="checkbox"
            checked={selectAll}
            onChange={handleSelectAll}
          />
          <div className="file-info">
            <strong></strong>
          </div>
        </div>
        {files.map((file, index) => (
          <div className="file-item" key={index}>
            <input
              type="checkbox"
              checked={file.checked}
              onChange={() => handleCheck(index)}
            />
            <div className="file-info">{file.name}</div>
            <div className="file-info">{file.type}</div>
            <div className="file-info">{file.date}</div>
            <div className="file-info">{file.size}</div>
          </div>
        ))}
      </div>
      <button className="download-button">DOWNLOAD</button>
    </div>
  );
}

export default Access;
