import React, { useState } from "react";
import "../CSS/Uploaded.css";

const Uploaded = () => {
  const [code, setCode] = useState("318278");

  const copyCodeToClipboard = () => {
    navigator.clipboard.writeText(code);
    alert("Code copied to clipboard!");
  };

  const copyLinkToClipboard = () => {
    const currentUrl = window.location.href;
    navigator.clipboard.writeText(`${currentUrl}/${code}`);
    alert("Link copied to clipboard!");
  };

  // Placeholder function for QR code generation
  const generateQRCode = () => {
    // Implementation will be added later
  };

  return (
    <div className="upload-container">
      <div className="progress-bar">
        <div className="progress" style={{ width: "0%" }}></div>
      </div>
      <div className="code-box">{code}</div>
      <div className="button-container">
        <button onClick={copyCodeToClipboard}>Copy</button>
        <button onClick={copyLinkToClipboard}>Get sharable link</button>
        <button onClick={generateQRCode}>Generate QR Code</button>
      </div>
    </div>
  );
};

export default Uploaded;
